    private void insertFolder(SQLiteDatabase db, long folderID, String folderName) {
        long now = System.currentTimeMillis();
        db.execSQL("INSERT INTO " + Database.FOLDER_TABLE_NAME + " VALUES (" + folderID + ", " + now + ", '" + folderName + "');");
    }

    private void insertSudoku(SQLiteDatabase db, long folderID, long sudokuID, String sudokuName, String data) {
        String sql = "INSERT INTO " + Database.SUDOKU_TABLE_NAME + " VALUES (" + sudokuID + ", " + folderID + ", 0, " + SudokuGame.GAME_STATE_NOT_STARTED + ", 0, null, '" + data + "', null);";
        db.execSQL(sql);}

    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        Log.i(TAG, "Upgrading database from version " + oldVersion + " to "
                + newVersion + "");
        if (oldVersion <=7 ) {
            createIndexes(db);
        }

        if (oldVersion <= 8) {
            db.execSQL("ALTER TABLE " + Database.SUDOKU_TABLE_NAME + " ADD COLUMN " + SudokuColumns.COMMAND_STACK + " TEXT");
        }
    }

    private void createIndexes(SQLiteDatabase db) {
        db.execSQL("create index " + Database.SUDOKU_TABLE_NAME +
                "_idx1 on " +
                Database.SUDOKU_TABLE_NAME + " (" + SudokuColumns.FOLDER_ID + ");");
    }
